// Função auto-invocável que recebe o objeto jQuery como argumento
!function (e) {
    "use strict";
    
    // Quando um link com a classe "js-scroll-trigger" é clicado, a seguinte função é executada
    e('a.js-scroll-trigger[href*="#"]:not([href="#"])').click(function () {
        
        // Verifica se o link é para uma âncora na página atual
        if (location.pathname.replace(/^\//, "") == this.pathname.replace(/^\//, "") && location.hostname == this.hostname) {
            
            // Armazena o destino da âncora em uma variável
            var t = e(this.hash);
            
            // Se o destino da âncora existir na página
            if ((t = t.length ? t : e("[name=" + this.hash.slice(1) + "]")).length) {
                
                // Anima a rolagem da página para o destino da âncora
                return e("html, body").animate({ scrollTop: t.offset().top }, 1e3, "easeInOutExpo"), !1
            }
        }
    });
    
    // Quando um link com a classe "js-scroll-trigger" é clicado, o menu de navegação é fechado
    e(".js-scroll-trigger").click(function () {
        e(".navbar-collapse").collapse("hide")
    });
    
    // Ativa a função "scrollspy" do Bootstrap no elemento "body" usando a seção de navegação com o ID "sideNav" como referência
    e("body").scrollspy({ target: "#sideNav" })
}(jQuery);
